                                            if ((include_labor_supply==1) .AND. (use_Frisch_elas==1)) then
                                            
                                                future_leisure_vector(future_mortgage_index)=&
                                                    future_leisure_vector(future_mortgage_index)+&
                                                    discount_factor*survival_probability(age_index)*&
                                                    employment_process(age_index,employment_index,future_employment_index)*&
                                                    min(value_function_leisure(future_years_of_amortization_index)%&
                                                    vector_transition_real(min(time_period+solve_transition_dynamics,&
                                                    transition_length),age_index+1,future_mortgage_index,&
                                                    fixed_employment_index,future_employment_index,&
                                                    future_housing_status_index),0.d0)
                                            
                                            end if
                                            
                                        end do
                                                                            
                                        value_function_owner_owner_move=flow_utility_vector(future_mortgage_index)+&
                                            future_utility_vector(future_mortgage_index)
                                                                                  
                                        if (value_function_owner_owner_move>=largest_value) then
                                    
                                            largest_value=value_function_owner_owner_move
                                            largest_value_bequests=future_bequest_vector(future_mortgage_index)
                                            largest_value_leisure=future_leisure_vector(future_mortgage_index)
                                                                              
                                            optimal_owner_owner_move_indeces(1)=future_mortgage_index
                                            optimal_owner_owner_move_indeces(2)=future_years_of_amortization_index
                                            optimal_owner_owner_move_indeces(3)=future_housing_status_index
                            
                                            optimal_consumption_owner_owner_move=&
                                                consumption_vector(optimal_owner_owner_move_indeces(1))
                                                
                                            optimal_after_tax_wealth_net_of_expenses_owner_owner_move=&
                                                after_tax_wealth_net_of_expenses
                                                
                                            optimal_gross_wage_income_owner_owner_move=gross_wage_income
                                            optimal_cutoff_taxable_income_owner_owner_move=cutoff_taxable_income
                                            optimal_wage_income_tax_paid_owner_owner_move=wage_income_tax_paid
                                            optimal_space_lived_in_owner_owner_move=space_lived_in_index_feasible
                                            optimal_taxable_rental_income_owner_owner_move=taxable_rental_income
                                            optimal_mortgage_deductions_owner_owner_move=mortgage_deductions_tax_rebates
                                            optimal_household_can_consume_owner_owner_move=1
                                            optimal_labor_supply_owner_owner_move=labor_supply_1
                                                                                                      
                                        end if
                                                                                    
                                    end if
                                    
                                end if
                                
                            end do
                                                            
                        end if
                        
                    end do
                           
                end do future_mortgage_loop_2
                
            end if
    
        end do future_housing_status_loop_1
        
        if (largest_value_bequests>0) then
        
            write(*,*) "odddd 555"
    
        end if
                    
        value_function_owner_owner_move=largest_value
        value_function_bequest_owner_owner_move=largest_value_bequests
        value_function_leisure_owner_owner_move=largest_value_leisure
                                                                
    end subroutine
    
    subroutine owner_renter_problem(time_period,financial_index)
    
        use Global_Vars
        
        implicit none
            
        integer :: time_period,financial_index,tenure_1,tenure_2,proceed_2,is_index_below_1,not_allowed_to_move_back_1
        real(8) :: after_tax_wealth_net_of_expenses_owner_renter,labor_supply_1,labor_supply_2
        
        not_allowed_to_move_back_1=0
        
        if ((age_index>=cannot_move_back_age) .OR. (employment_index>index_below_can_pick_move_back)) then
        
            not_allowed_to_move_back_1=1
            
        end if
        
        tenure_1=1
        tenure_2=0
        proceed_2=1
        
        is_index_below_1=include_those_with_exogeous_labor_supply

        labor_supply_2=1
        
        largest_value=large_negative_num
        largest_value_bequests=0
        largest_value_leisure=0
                        
        optimal_owner_renter_indeces(1)=negative_financial_grid_size
        optimal_owner_renter_indeces(2)=1
        optimal_owner_renter_indeces(3)=0
                                    
        optimal_consumption_owner_renter=0
        optimal_gross_wage_income_owner_renter=0
        optimal_cutoff_taxable_income_owner_renter=0
        optimal_wage_income_tax_paid_owner_renter=0
        optimal_after_tax_wealth_net_of_expenses_owner_renter=0
        optimal_after_tax_wealth_owner_renter=0
        optimal_space_lived_in_owner_renter=0
        optimal_taxable_rental_income_owner_renter=0
        optimal_mortgage_deductions_owner_renter=0
        optimal_rent_subsidy_owner_renter=0
        optimal_household_can_consume_owner_renter=0
        optimal_labor_supply_owner_renter=(1-include_labor_supply)*1+include_labor_supply*(in_retirement*0+(1-in_retirement)*1)
        labor_supply_1=optimal_labor_supply_owner_renter
        
        future_housing_status_loop_1: do future_housing_status_index=&
            (1-not_allowed_to_move_back_1)*lowest_housing_grid_point,largest_house_size_hhld_can_rent
                        
            do future_financial_index=negative_financial_grid_size,financial_grid_size
            
                if ((include_labor_supply==1) .AND. (in_retirement==0)) then
                
                    if (employment_index<=index_below_can_pick_labor) then
                    
                        labor_supply_2=1
                        is_index_below_1=1
                        
                    else
                    
                        labor_supply_2=avg_labor_supply_data*max_labor_supply
                                                        
                    end if
                
                end if
                        
                call calculate_after_tax_wealth&
                    (financial_index,future_financial_index,years_left_on_mortgage_index,0,&
                    housing_status_index,future_housing_status_index,future_housing_status_index,&
                    tenure_2,tenure_1,tenure_2,tenure_2,tenure_2,time_period,labor_supply_2,cutoff_taxable_income)
                    ! owner_to_owner_move_1,owner_to_renter_1,renter_to_owner_1,owner_to_owner_stay_1,renter_to_renter_1
                    
                if ((include_labor_supply==1) .AND. (in_retirement==0)) then
                
                    if (employment_index<=index_below_can_pick_labor) then
                
                        call secant(time_period,financial_index,financial_grid(future_financial_index),&
                            housing_size(future_housing_status_index),after_tax_wealth_net_of_expenses,&
                            labor_supply_1,gross_wage_income,wage_income_tax_paid,mortgage_deductions_tax_rebates,&
                            cutoff_taxable_income,use_Frisch_elas,proceed_2)
                            
                    else
                    
                        labor_supply_1=labor_supply_2
                            
                    end if
                                
                end if
                                                                                 
                if ((future_housing_status_index==0) .AND. &
                    (after_tax_wealth_net_of_expenses<min_consumption_guaranteed) .AND. &
                    (age_index>=1) .AND. (solve_transition_dynamics==1) .AND. &
                    (add_utility_from_child==0)) then
                    
                    rent_subsidy=0
                                        
                    after_tax_wealth_net_of_expenses_owner_renter=&
                        after_tax_wealth_net_of_expenses+rent_subsidy
                                                                       
                else
                                    
                    rent_subsidy=0
                    after_tax_wealth_net_of_expenses_owner_renter=&
                        after_tax_wealth_net_of_expenses
                    
                end if
                            
                if (-financial_grid(financial_index)/(housing_price(time_period)*&
                    housing_size(housing_status_index))>0.8) then
                                                
                    pay_mortgage_insurance=1
                        
                else
                                
                    pay_mortgage_insurance=0
                                    
                end if
                                                    
                consumption_vector(future_financial_index)=(after_tax_wealth_net_of_expenses_owner_renter-&
                    financial_grid(future_financial_index))/(1+consumption_tax)
                                                      
                if ((consumption_vector(future_financial_index)>0) .AND. (proceed_2==1)) then
                
                    future_leisure_vector(future_financial_index)=0
                
                    if ((include_labor_supply==1) .AND. (use_Frisch_elas==1)) then
                    
                        future_leisure_vector(future_financial_index)=&
                            is_index_below_1*include_labor_supply*relative_share_of_leisure*&
                            ((max_labor_supply-labor_supply_1)**(1-Frisch_elasticity))/(1-Frisch_elasticity)
                    
                        flow_utility_vector(future_financial_index)=&
                            (housing_status_utility_function(future_housing_status_index,0)*&
                            (consumption_vector(future_financial_index)**&
                            ((1-risk_aversion_consumption)*(1-relative_share_of_housing))))/&
                            (1-risk_aversion_consumption)+future_leisure_vector(future_financial_index)
                            
                    else
                                                           
                        flow_utility_vector(future_financial_index)=&
                            (housing_status_utility_function(future_housing_status_index,0)*&
                            (max_labor_supply-include_labor_supply*labor_supply_1)**&
                            (relative_share_of_leisure*(1-risk_aversion_consumption))*&
                            (consumption_vector(future_financial_index))**&
                            ((1-risk_aversion_consumption)*(1-relative_share_of_housing-&
                            relative_share_of_leisure)))/(1-risk_aversion_consumption)
                            
                    end if
                                                
                    flow_utility_vector(future_financial_index)=flow_utility_vector(future_financial_index)*&
                        scaled_household_size(age_index)
                                                                                                            
                    future_utility_vector=0
                    future_bequest_vector=0
                           
                    if ((add_utility_from_child==1) .OR. (add_net_worth_bequest>=1)) then
                            
                        if ((add_net_worth_bequest==1) .AND. &
                            ((age_index==life_span) .OR. (have_probablistic_death==1))) then
                        
                            if (minimum_inheritance>0) then
        
                                future_bequest_vector(future_financial_index)=&
                                    (1-survival_probability(age_index))*discount_factor_on_child*&
                                    ((minimum_inheritance+financial_grid(future_financial_index))**&
                                    (1-risk_aversion_consumption))/(1-risk_aversion_consumption)
                                    
                            else
                            
                                if (financial_grid(future_financial_index)>0) then
                                
                                    future_bequest_vector(future_financial_index)=&
                                        (1-survival_probability(age_index))*discount_factor_on_child*&
                                        (financial_grid(future_financial_index)**&
                                        (1-risk_aversion_consumption))/(1-risk_aversion_consumption)
                                
                                else
                                
                                    future_utility_vector(future_financial_index)=large_negative_num
                                
                                end if
                                
                            end if
                            
                            if (future_bequest_vector(future_financial_index)>0) then
        
                                write(*,*) "odddd 111"
                        
                            end if
                            
                            future_utility_vector(future_financial_index)=&
                                future_utility_vector(future_financial_index)+&
                                future_bequest_vector(future_financial_index)
                                    
                        elseif (add_net_worth_bequest==2) then
                        
                            do future_fixed_employment_index=1,fixed_employment_grid_size
                                                                        
                                do future_employment_index=1,employment_grid_size
                                                                                     
                                    future_utility_vector(future_financial_index)=&
                                        future_utility_vector(future_financial_index)+&                                                           
                                        discount_factor_on_child*(1-survival_probability(age_index))*&
                                        unconditional_employment_probability(future_employment_index)*&
                                        unconditional_fixed_employment_probability(future_fixed_employment_index)*&
                                        value_function_guess(1)%vector_youngest_real&
                                        (future_financial_index,future_fixed_employment_index,future_employment_index,&
                                        track_renters_housing*future_housing_status_index)
                                        
                                end do
                                    
                            end do
                            
                        end if
                                                                                        
                    end if
                                                                    
                    do future_employment_index=&
                        at_least_one_period_before_retirement*employment_index+&
                        (1-at_least_one_period_before_retirement)*1,&
                        at_least_one_period_before_retirement*employment_index+&
                        (1-at_least_one_period_before_retirement)*employment_grid_size
                                                                                                                                                                                       
                        future_utility_vector(future_financial_index)=&
                            future_utility_vector(future_financial_index)+&
                            discount_factor*survival_probability(age_index)*&
                            employment_process(age_index,employment_index,future_employment_index)*&
                            value_function(1)%vector_transition_real&
                            (min(time_period+solve_transition_dynamics,transition_length),&
                            age_index+1,future_financial_index,fixed_employment_index,future_employment_index,&
                            track_renters_housing*future_housing_status_index)
                            
                        if ((add_net_worth_bequest==1) .AND. &
                            ((age_index==life_span) .OR. (have_probablistic_death==1))) then
                            
                            future_bequest_vector(future_financial_index)=&
                                future_bequest_vector(future_financial_index)+&
                                discount_factor*survival_probability(age_index)*&
                                employment_process(age_index,employment_index,future_employment_index)*&
                                min(value_function_bequests(1)%vector_transition_real&
                                (min(time_period+solve_transition_dynamics,transition_length),&
                                age_index+1,future_financial_index,fixed_employment_index,future_employment_index,&
                                track_renters_housing*future_housing_status_index),0.d0)
                                
                        end if
                            
                        if ((include_labor_supply==1) .AND. (add_net_worth_bequest==1)) then
                        
                            future_leisure_vector(future_financial_index)=&
                                future_leisure_vector(future_financial_index)+&
                                discount_factor*survival_probability(age_index)*&
                                employment_process(age_index,employment_index,future_employment_index)*&
                                min(value_function_leisure(1)%vector_transition_real&
                                (min(time_period+solve_transition_dynamics,transition_length),&
                                age_index+1,future_financial_index,fixed_employment_index,future_employment_index,&
                                track_renters_housing*future_housing_status_index),0.d0)
                        
                        end if
                                                                                                                                                                                                            
                    end do
                    
                    if (future_bequest_vector(future_financial_index)>0) then
        
                        write(*,*) "odddd -2"
                
                    end if
                                                        
                    value_function_owner_renter=flow_utility_vector(future_financial_index)+&
                        future_utility_vector(future_financial_index)
                                                           
                    if (value_function_owner_renter>=largest_value) then
                
                        largest_value=value_function_owner_renter
                        largest_value_bequests=future_bequest_vector(future_financial_index)
                        largest_value_leisure=future_leisure_vector(future_financial_index)
                                                             
                        optimal_owner_renter_indeces(1)=future_financial_index
                        optimal_owner_renter_indeces(2)=1
                        optimal_owner_renter_indeces(3)=future_housing_status_index
        
                        optimal_consumption_owner_renter=consumption_vector(future_financial_index)
                        
                        optimal_after_tax_wealth_net_of_expenses_owner_renter=&
                            after_tax_wealth_net_of_expenses_owner_renter
                            
                        optimal_gross_wage_income_owner_renter=gross_wage_income
                        optimal_cutoff_taxable_income_owner_renter=cutoff_taxable_income
                        optimal_wage_income_tax_paid_owner_renter=wage_income_tax_paid
                        optimal_space_lived_in_owner_renter=future_housing_status_index
                        optimal_taxable_rental_income_owner_renter=taxable_rental_income
                        optimal_mortgage_deductions_owner_renter=mortgage_deductions_tax_rebates
                        optimal_household_can_consume_owner_renter=1
                        optimal_rent_subsidy_owner_renter=rent_subsidy
                        optimal_labor_supply_owner_renter=labor_supply_1
                        
                    end if
                                                                    
                end if
                
            end do
                                                                 
        end do future_housing_status_loop_1
        
        if (largest_value_bequests>0) then
        
            write(*,*) "odddd 666"
    
        end if
        
        value_function_owner_renter=largest_value
        value_function_bequest_owner_renter=largest_value_bequests
        value_function_leisure_owner_renter=largest_value_leisure
                            
    end subroutine
    
    subroutine which_is_better(time_period,financial_index)
    
        use Global_Vars
        
        implicit none
        
        integer :: time_period,financial_index,is_owner_better
        
        is_owner_better=0
        
        if (value_function_owner>value_function_renter) then
        
            is_owner_better=1
            
        end if
                                                                                                
        value_function(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*value_function_owner+(1-is_owner_better)*value_function_renter
            
        value_function_bequests(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*value_function_bequest_owner+(1-is_owner_better)*value_function_bequest_renter
            
        if (value_function_bequests(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)>0) then
            
            write(*,*) "strange"
            write(*,*) "is_owner_better*value_function_bequest_owner+&
                (1-is_owner_better)*value_function_bequest_renter=", &
                is_owner_better*value_function_bequest_owner+(1-is_owner_better)*value_function_bequest_renter
            write(*,*) "value_function_bequest_renter=", value_function_bequest_renter
            write(*,*) "value_function_bequest_owner=", value_function_bequest_owner
            write(*,*) "value_function_bequests(years_left_on_mortgage_index)%&
                vector_transition_real(time_period,age_index,financial_index,&
                fixed_employment_index,employment_index,housing_status_index)=", &
                value_function_bequests(years_left_on_mortgage_index)%&
                vector_transition_real(time_period,age_index,financial_index,&
                fixed_employment_index,employment_index,housing_status_index)
            
        end if
            
        value_function_leisure(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*value_function_leisure_owner+(1-is_owner_better)*value_function_leisure_renter
            
        optimal_after_tax_wealth_net_of_expenses=&
            is_owner_better*optimal_after_tax_wealth_net_of_expenses_owner+&
            (1-is_owner_better)*optimal_after_tax_wealth_net_of_expenses_renter
            
        policy_function_financial_index(years_left_on_mortgage_index)%&
            vector_transition_integer(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_owner_indeces(1)+(1-is_owner_better)*optimal_renter_indeces(1)
            
        policy_function_years_of_amortization_index(years_left_on_mortgage_index)%&
            vector_transition_integer(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_owner_indeces(2)+(1-is_owner_better)*optimal_renter_indeces(2)
                                        
        policy_function_housing_status_index(years_left_on_mortgage_index)%&
            vector_transition_integer(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_owner_indeces(3)+(1-is_owner_better)*optimal_renter_indeces(3)
                                
        policy_function_consumption(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_consumption_owner+(1-is_owner_better)*optimal_consumption_renter
                    
        policy_function_space_lived_in_index(years_left_on_mortgage_index)%&
            vector_transition_integer(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_space_lived_in_owner+(1-is_owner_better)*optimal_space_lived_in_renter
            
        if (housing_size(optimal_owner_indeces(3))-housing_size(optimal_space_lived_in_owner)>0) then
        
            if (((rent(1)*(housing_size(optimal_owner_indeces(3))-housing_size(optimal_space_lived_in_owner))-&
                landlord_fixed_costs)<0) .AND. (housing_price(time_period)>calibrated_stationary_housing_price*1)) then
                
                write(*,*) "wrong"
                write(*,*) "rent(1)=", rent(1)
                write(*,*) "housing_size(optimal_owner_indeces(3))-housing_size(optimal_space_lived_in_owner)=", &
                    housing_size(optimal_owner_indeces(3))-housing_size(optimal_space_lived_in_owner)
                write(*,*) "landlord_fixed_costs=", landlord_fixed_costs
                
            end if
        
        end if
        
        policy_function_space_lived_in_size(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*housing_size(optimal_space_lived_in_owner)+&
            (1-is_owner_better)*housing_size(optimal_space_lived_in_renter)
            
        policy_function_taxable_rental_income(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_taxable_rental_income_owner+&
            (1-is_owner_better)*optimal_taxable_rental_income_renter
                        
        policy_function_wage_income_tax_paid(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_wage_income_tax_paid_owner+&
            (1-is_owner_better)*optimal_wage_income_tax_paid_renter
            
        if (is_owner_better==0) then
        
            if (years_left_on_mortgage_index>1) then
                   
                policy_function_mortgage_interest_deductions(years_left_on_mortgage_index)%&
                    vector_transition_real(time_period,age_index,financial_index,&
                    fixed_employment_index,employment_index,housing_status_index)=&
                    optimal_mortgage_deductions_renter
                    
            else
            
                policy_function_mortgage_interest_deductions(years_left_on_mortgage_index)%&
                    vector_transition_real(time_period,age_index,financial_index,&
                    fixed_employment_index,employment_index,housing_status_index)=0
            
            end if
        
        else
        
            policy_function_mortgage_interest_deductions(years_left_on_mortgage_index)&
                %vector_transition_real(time_period,age_index,financial_index,&
                fixed_employment_index,employment_index,housing_status_index)=&
                optimal_mortgage_deductions_owner
                
        end if
                                            
        policy_function_rent_subsidy(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_rent_subsidy_owner+&
            (1-is_owner_better)*optimal_rent_subsidy_renter
            
        policy_function_labor_supply(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_labor_supply_owner+&
            (1-is_owner_better)*optimal_labor_supply_renter

        policy_function_cutoff_taxable_income(years_left_on_mortgage_index)%&
            vector_transition_real(time_period,age_index,financial_index,&
            fixed_employment_index,employment_index,housing_status_index)=&
            is_owner_better*optimal_cutoff_taxable_income_owner+&
            (1-is_owner_better)*optimal_cutoff_taxable_income_renter
               
        if (is_owner_better==0) then
                
            if (((housing_size(optimal_renter_indeces(3))-housing_size(optimal_space_lived_in_renter))>0) .AND. &
                (optimal_renter_indeces(2)<=1)) then
                                
                write(*,*) "wrong"
                write(*,*) "housing_size(optimal_renter_indeces(3))", housing_size(optimal_renter_indeces(3))
                write(*,*) "housing_size(optimal_space_lived_in_renter)=", housing_size(optimal_space_lived_in_renter)
                write(*,*) "years_left_on_mortgage_index=", years_left_on_mortgage_index
                write(*,*) "optimal_renter_indeces(2)=", optimal_renter_indeces(2)
                write(*,*) "optimal_after_tax_wealth_net_of_expenses_renter=", &
                    optimal_after_tax_wealth_net_of_expenses_renter
                                
            end if
                
            if ((policy_function_taxable_rental_income(years_left_on_mortgage_index)%&
                vector_transition_real(time_period,age_index,financial_index,&
                fixed_employment_index,employment_index,housing_status_index)<0) .AND. &
                (optimal_renter_indeces(2)<=1)) then
                
                write(*,*) "dosadoasisdof renter"
                write(*,*) "age=", age_index
                
            end if
            
            if ((policy_function_taxable_rental_income(years_left_on_mortgage_index)%&
                vector_transition_real(time_period,age_index,financial_index,&
                fixed_employment_index,employment_index,housing_status_index)>0) .AND. &
                (optimal_renter_indeces(2)<=1)) then
                
                write(*,*) "dosadoasisdof renter positive rental income"
                write(*,*) "age=", age_index
                
            end if
            
        end if
                                                                 
    end subroutine
    
    subroutine secant(time_period,financial_index_1,future_financial_amount_1,space_lived_in_1,&
        after_tax_wealth_net_of_expenses_2,labor_supply_1,gross_wage_income_2,wage_income_tax_paid_2,&
        mortgage_deductions_tax_rebates_2,cutoff_taxable_income_2,use_Frisch_elas_2,proceed_2)
    
        use Global_Vars
    
        implicit none
        
        real(8) :: y_0,y_1,x_0,x_1,x_2
        real(8) :: stopping_rule_secant=10.d0**(-3)
        real(8) :: distance_secant
        real(8), intent(inout) :: labor_supply_1,after_tax_wealth_net_of_expenses_2,gross_wage_income_2,&
            wage_income_tax_paid_2,mortgage_deductions_tax_rebates_2
        real(8), intent(in) :: future_financial_amount_1,space_lived_in_1,cutoff_taxable_income_2
        integer, intent(in) :: financial_index_1,use_Frisch_elas_2
        integer, intent(inout) :: proceed_2
        integer :: is_x_1_constrained,time_period,loop_1,can_consume_0,can_consume_1,&
            use_other_foc_1,is_taxable_income_positive_0,is_taxable_income_positive_1,include_tax_2
        real(8) :: after_tax_wealth_net_of_expenses_0,after_tax_wealth_net_of_expenses_1,&
            gross_wage_income_0,wage_income_tax_paid_0,gross_wage_income_1,wage_income_tax_paid_1,&
            mortgage_deductions_tax_rebates_0,mortgage_deductions_tax_rebates_1,smallest_labor_supply_2
            
        smallest_labor_supply_2=cutoff_taxable_income_2/(fixed_employment_grid(fixed_employment_index)*&
            employment_grid(age_index,employment_index)*labour_deterministic_efficiency(age_index))
            
        if (use_Frisch_elas_2==0) then
        
            proceed_2=1
        
            include_tax_2=1
            
            use_other_foc_1=0

            x_1=max(min((relative_share_of_leisure*&
                (after_tax_wealth_net_of_expenses_2-future_financial_amount_1+&
                (1-use_flat_income_tax)*wage_income_tax_paid_2))/&
                ((1-relative_share_of_housing+consumption_tax*&
                (1-relative_share_of_leisure-relative_share_of_housing))*(gross_wage_income_2*&
                ((1-use_flat_income_tax)+use_flat_income_tax*labor_income_tax_rate))),max_labor_supply),0.d0)

            x_1=max_labor_supply-x_1
            x_0=x_1

            after_tax_wealth_net_of_expenses_0=after_tax_wealth_net_of_expenses_2
            
            gross_wage_income_0=gross_wage_income_2
            wage_income_tax_paid_0=wage_income_tax_paid_2
            
            mortgage_deductions_tax_rebates_0=mortgage_deductions_tax_rebates_2

            call objective(time_period,y_0,x_0,after_tax_wealth_net_of_expenses_0,&
                financial_index_1,future_financial_amount_1,space_lived_in_1,&
                can_consume_0,gross_wage_income_0,wage_income_tax_paid_0,&
                mortgage_deductions_tax_rebates_0,is_taxable_income_positive_0,&
                use_Frisch_elas_2,include_tax_2) 

            if ((is_taxable_income_positive_0==1) .AND. (use_flat_income_tax==0)) then

                use_other_foc_1=1
            
            end if

            if (use_other_foc_1==0) then

                after_tax_wealth_net_of_expenses_2=after_tax_wealth_net_of_expenses_0
            
                gross_wage_income_2=gross_wage_income_0
                wage_income_tax_paid_2=wage_income_tax_paid_0
                
                mortgage_deductions_tax_rebates_2=mortgage_deductions_tax_rebates_0 

                labor_supply_1=x_1

            else
                    
                is_x_1_constrained=1
                distance_secant=1
                
                x_0=0.99*max_labor_supply
                x_1=0.95*max_labor_supply
                        
                loop_1=1
                    
                do while (distance_secant>stopping_rule_secant)
                
                    after_tax_wealth_net_of_expenses_0=after_tax_wealth_net_of_expenses_2
                    after_tax_wealth_net_of_expenses_1=after_tax_wealth_net_of_expenses_2
                    
                    gross_wage_income_0=gross_wage_income_2
                    wage_income_tax_paid_0=wage_income_tax_paid_2
                    gross_wage_income_1=gross_wage_income_2
                    wage_income_tax_paid_1=wage_income_tax_paid_2
                    
                    mortgage_deductions_tax_rebates_0=mortgage_deductions_tax_rebates_2
                    mortgage_deductions_tax_rebates_1=mortgage_deductions_tax_rebates_2
                            
                    if (x_1 .NE. x_0) then
                                
                        call objective(time_period,y_0,x_0,after_tax_wealth_net_of_expenses_0,&
                            financial_index_1,future_financial_amount_1,space_lived_in_1,&
                            can_consume_0,gross_wage_income_0,wage_income_tax_paid_0,&
                            mortgage_deductions_tax_rebates_0,is_taxable_income_positive_0,&
                            use_Frisch_elas_2,include_tax_2)
                            
                        call objective(time_period,y_1,x_1,after_tax_wealth_net_of_expenses_1,&
                            financial_index_1,future_financial_amount_1,space_lived_in_1,&
                            can_consume_1,gross_wage_income_1,wage_income_tax_paid_1,&
                            mortgage_deductions_tax_rebates_1,is_taxable_income_positive_1,&
                            use_Frisch_elas_2,include_tax_2)

                        if ((x_0<=smallest_labor_supply_2) .OR. (x_1<=smallest_labor_supply_2) .OR. &
                            (isnan(x_0)) .OR. (isnan(x_1))) then

                            distance_secant=0

                        else

                            if (can_consume_1==0) then
                            
                                x_1=(x_0+x_1)/2
                                
                                distance_secant=abs(x_0-x_1)   
                                            
                            else

                                if (y_1 .NE. y_0) then
                                
                                    x_2=x_1-((x_1-x_0)/(y_1-y_0))*y_1
                                    
                                    x_0=x_1
                                    x_1=x_2

                                    distance_secant=abs(x_0-x_1)   

                                else

                                    distance_secant=0
                                                                                    
                                end if   
                                    
                            end if

                        end if

                        loop_1=loop_1+1
                                                    
                    end if
                                                    
                end do

                if ((x_0<=smallest_labor_supply_2) .OR. (x_1<=smallest_labor_supply_2) .OR. &
                   (isnan(x_0)) .OR. (isnan(x_1))) then

                    x_1=0
                    labor_supply_1=0
                    proceed_2=0
                    
                else

                    if (is_x_1_constrained==1) then
              
                        x_1=min(max(x_1,0.d0),max_labor_supply)

                    end if
                        
                    labor_supply_1=x_1
                    
                    call objective(time_period,y_1,labor_supply_1,after_tax_wealth_net_of_expenses_2,&
                        financial_index_1,future_financial_amount_1,space_lived_in_1,&
                        can_consume_1,gross_wage_income_2,wage_income_tax_paid_2,&
                        mortgage_deductions_tax_rebates_2,is_taxable_income_positive_1,&
                        use_Frisch_elas_2,include_tax_2)

                end if

            end if
            
        else
             
            proceed_2=1
            use_other_foc_1=0 
            include_tax_2=1
            
            is_x_1_constrained=1
            distance_secant=1
            
            x_0=0.7*max_labor_supply
            x_1=0.5*max_labor_supply
                        
            loop_1=1
                
            do while (distance_secant>stopping_rule_secant)
            
                after_tax_wealth_net_of_expenses_0=after_tax_wealth_net_of_expenses_2
                after_tax_wealth_net_of_expenses_1=after_tax_wealth_net_of_expenses_2
                
                gross_wage_income_0=gross_wage_income_2
                wage_income_tax_paid_0=wage_income_tax_paid_2
                gross_wage_income_1=gross_wage_income_2
                wage_income_tax_paid_1=wage_income_tax_paid_2
                
                mortgage_deductions_tax_rebates_0=mortgage_deductions_tax_rebates_2
                mortgage_deductions_tax_rebates_1=mortgage_deductions_tax_rebates_2
                        
                if (x_1 .NE. x_0) then
                            
                    call objective(time_period,y_0,x_0,after_tax_wealth_net_of_expenses_0,&
                        financial_index_1,future_financial_amount_1,space_lived_in_1,&
                        can_consume_0,gross_wage_income_0,wage_income_tax_paid_0,&
                        mortgage_deductions_tax_rebates_0,is_taxable_income_positive_0,&
                        use_Frisch_elas_2,include_tax_2)
                        
                    call objective(time_period,y_1,x_1,after_tax_wealth_net_of_expenses_1,&
                        financial_index_1,future_financial_amount_1,space_lived_in_1,&
                        can_consume_1,gross_wage_income_1,wage_income_tax_paid_1,&
                        mortgage_deductions_tax_rebates_1,is_taxable_income_positive_1,&
                        use_Frisch_elas_2,include_tax_2)

                    if ((x_0<=smallest_labor_supply_2) .OR. (x_1<=smallest_labor_supply_2) .OR. &
                        (isnan(x_0)) .OR. (isnan(x_1))) then

                        distance_secant=0

                    else

                        if (can_consume_1==0) then
                        
                            x_1=(x_0+x_1)/2
                            
                            distance_secant=abs(x_0-x_1)   
                                        
                        else

                            if (y_1 .NE. y_0) then
                            
                                x_2=x_1-((x_1-x_0)/(y_1-y_0))*y_1
                                
                                x_0=x_1
                                x_1=x_2

                                distance_secant=abs(x_0-x_1)   

                            else

                                distance_secant=0
                                                                                
                            end if   
                                
                        end if

                    end if

                    loop_1=loop_1+1
                                                
                end if
                                                
            end do

            if ((x_0<=smallest_labor_supply_2) .OR. (x_1<=smallest_labor_supply_2) .OR. &
               (isnan(x_0)) .OR. (isnan(x_1))) then

                use_other_foc_1=1
                labor_supply_1=0
                
            else
            
                if (is_x_1_constrained==1) then
          
                    x_1=min(max(x_1,0.d0),max_labor_supply)

                end if
                
                labor_supply_1=x_1
                
                call objective(time_period,y_1,labor_supply_1,after_tax_wealth_net_of_expenses_2,&
                    financial_index_1,future_financial_amount_1,space_lived_in_1,&
                    can_consume_1,gross_wage_income_2,wage_income_tax_paid_2,&
                    mortgage_deductions_tax_rebates_2,is_taxable_income_positive_1,&
                    use_Frisch_elas_2,include_tax_2)

            end if

            if (use_other_foc_1==1) then
            
                include_tax_2=0
            
                is_x_1_constrained=1
                distance_secant=1
                
                x_0=1*smallest_labor_supply_2
                x_1=0.8*smallest_labor_supply_2
                            
                loop_1=1
                    
                do while (distance_secant>stopping_rule_secant)
                
                    after_tax_wealth_net_of_expenses_0=after_tax_wealth_net_of_expenses_2
                    after_tax_wealth_net_of_expenses_1=after_tax_wealth_net_of_expenses_2
                    
                    gross_wage_income_0=gross_wage_income_2
                    wage_income_tax_paid_0=wage_income_tax_paid_2
                    gross_wage_income_1=gross_wage_income_2
                    wage_income_tax_paid_1=wage_income_tax_paid_2
                    
                    mortgage_deductions_tax_rebates_0=mortgage_deductions_tax_rebates_2
                    mortgage_deductions_tax_rebates_1=mortgage_deductions_tax_rebates_2
                            
                    if (x_1 .NE. x_0) then
                                
                        call objective(time_period,y_0,x_0,after_tax_wealth_net_of_expenses_0,&
                            financial_index_1,future_financial_amount_1,space_lived_in_1,&
                            can_consume_0,gross_wage_income_0,wage_income_tax_paid_0,&
                            mortgage_deductions_tax_rebates_0,is_taxable_income_positive_0,&
                            use_Frisch_elas_2,include_tax_2)
                            
                        call objective(time_period,y_1,x_1,after_tax_wealth_net_of_expenses_1,&
                            financial_index_1,future_financial_amount_1,space_lived_in_1,&
                            can_consume_1,gross_wage_income_1,wage_income_tax_paid_1,&
                            mortgage_deductions_tax_rebates_1,is_taxable_income_positive_1,&
                            use_Frisch_elas_2,include_tax_2)

                        if ((x_0>smallest_labor_supply_2) .OR. (x_1>smallest_labor_supply_2) .OR. &
                            (isnan(x_0)) .OR. (isnan(x_1))) then

                            distance_secant=0

                        else

                            if (can_consume_1==0) then
                            
                                x_1=(x_0+x_1)/2
                                
                                distance_secant=abs(x_0-x_1)   
                                            
                            else

                                if (y_1 .NE. y_0) then
                                
                                    x_2=x_1-((x_1-x_0)/(y_1-y_0))*y_1
                                    
                                    x_0=x_1
                                    x_1=x_2

                                    distance_secant=abs(x_0-x_1)   

                                else

                                    distance_secant=0
                                                                                    
                                end if   
                                    
                            end if

                        end if

                        loop_1=loop_1+1
                                                    
                    end if
                                                    
                end do

                if ((x_0>smallest_labor_supply_2) .OR. (x_1>smallest_labor_supply_2) .OR. &
                   (isnan(x_0)) .OR. (isnan(x_1))) then

                    labor_supply_1=0
                    proceed_2=0
                    
                else
                
                    if (is_x_1_constrained==1) then
              
                        x_1=min(max(x_1,0.d0),max_labor_supply)

                    end if
                    
                    labor_supply_1=x_1
                    
                    call objective(time_period,y_1,labor_supply_1,after_tax_wealth_net_of_expenses_2,&
                        financial_index_1,future_financial_amount_1,space_lived_in_1,&
                        can_consume_1,gross_wage_income_2,wage_income_tax_paid_2,&
                        mortgage_deductions_tax_rebates_2,is_taxable_income_positive_1,&
                        use_Frisch_elas_2,include_tax_2)
                        
                end if
                                    
            end if
    
        end if
                     
    end subroutine

    subroutine objective(time_period,y,labor_supply_1,after_tax_wealth_net_of_expenses_1,financial_index_1,&
        future_financial_amount_1,space_lived_in_1,can_consume_1,gross_wage_income_1,wage_income_tax_paid_1,&
        mortgage_deductions_tax_rebates_1,is_taxable_income_positive_1,use_Frisch_elas_1,include_taxes_1)
        
        use Global_Vars

        real(8), intent(out) :: y
        real(8), intent(inout) :: labor_supply_1,gross_wage_income_1,wage_income_tax_paid_1,&
            after_tax_wealth_net_of_expenses_1,mortgage_deductions_tax_rebates_1
        integer, intent(inout) :: can_consume_1
        integer, intent(out) :: is_taxable_income_positive_1
        real(8), intent(in) :: future_financial_amount_1,space_lived_in_1
        integer, intent(in) :: financial_index_1,use_Frisch_elas_1,include_taxes_1
        real(8) :: consumption_1,taxable_income_1
        integer :: time_period,mortgage_index_2,deduct_MID
        real(8) :: deductions_claimed_1,max_gross_wage_income_1,imputed_rent_taxes_paid_1,&
            tax_liability_with_mortgage_deductions_1,tax_liability_without_mortgage_deductions_1
          
        can_consume_1=1
          
        imputed_rent_taxes_paid_1=0
        
        if ((years_left_on_mortgage_index>1) .AND. (tax_imputed_rent==1)) then
                
            imputed_rent_taxes_paid_1=investment_income_tax*&
                max(space_lived_in_1*(rent(time_period)-&
                housing_price(time_period)*housing_depreciation_rate),0.d0)
                
        end if
                
        max_gross_wage_income_1=fixed_employment_grid(fixed_employment_index)*&
            employment_grid(age_index,employment_index)*labour_deterministic_efficiency(age_index)
        
        after_tax_wealth_net_of_expenses_1=after_tax_wealth_net_of_expenses_1-&
            (gross_wage_income_1-wage_income_tax_paid_1)

        gross_wage_income_1=labor_supply_1*max_gross_wage_income_1
                                
        mortgage_index_2=min(financial_index_1,negative_financial_grid_size)
                     
        deduct_MID=0
        deductions_claimed_1=0
        tax_liability_with_mortgage_deductions_1=0
        tax_liability_without_mortgage_deductions_1=0
        mortgage_deductions_tax_rebates_1=0
                                    
        if ((years_left_on_mortgage_index>1) .AND. ((mortgage_deductibility>0) .OR. (property_tax_deductibility>0))) then
                            
            deductions_claimed_1=&
                (mortgage_deductibility*min(-min(financial_grid(mortgage_index_2),0.d0),&
                max_MID_debt*(is_counterfactual*average_labor_income_calibrated+&
                (1-is_counterfactual)*model_average_labour_income(time_period)))*mortgage_interest_rate(time_period)+&
                (property_tax_deductibility*property_tax(time_period)+include_housing_depreciation_in_housing_costs*&
                housing_depreciation_rate)*housing_price(time_period)*space_lived_in_1)
                
            deductions_claimed_1=max(deductions_claimed_1,0.d0)
                
            if (deductions_claimed_1>standard_deduction) then
                
                deduct_MID=1
                                                                                    
                tax_liability_with_mortgage_deductions_1=&
                    max(max(gross_wage_income_1+add_imputed_rents_to_labor_income*imputed_rent_taxes_paid_1-&
                    personal_exemption-deductions_claimed_1,0.d0)-(labor_income_tax_rate+increase_in_labour_income_tax)*&
                    max(gross_wage_income_1+add_imputed_rents_to_labor_income*imputed_rent_taxes_paid_1-&
                    personal_exemption-deductions_claimed_1,0.d0)**curvature_of_labor_income_taxes,0.d0)
                                
                tax_liability_without_mortgage_deductions_1=&
                    max(max(gross_wage_income_1+add_imputed_rents_to_labor_income*imputed_rent_taxes_paid_1-&
                    personal_exemption-standard_deduction,0.d0)-(labor_income_tax_rate+increase_in_labour_income_tax)*&
                    max(gross_wage_income_1+add_imputed_rents_to_labor_income*imputed_rent_taxes_paid_1-&
                    personal_exemption-standard_deduction,0.d0)**curvature_of_labor_income_taxes,0.d0)
                    
                mortgage_deductions_tax_rebates_1=max(tax_liability_without_mortgage_deductions_1-&
                    tax_liability_with_mortgage_deductions_1,0.d0)
            
            end if
                     
        end if

        taxable_income_1=max(gross_wage_income_1-personal_exemption-&
            deduct_MID*deductions_claimed_1-(1-deduct_MID)*standard_deduction,0.d0)
                    
        wage_income_tax_paid_1=include_taxes_1*&
            max(max(taxable_income_1+add_imputed_rents_to_labor_income*imputed_rent_taxes_paid_1,0.d0)-&
            (labor_income_tax_rate+increase_in_labour_income_tax)*&
            max(taxable_income_1+add_imputed_rents_to_labor_income*imputed_rent_taxes_paid_1,0.d0)**&
            curvature_of_labor_income_taxes,0.d0)
            
        after_tax_wealth_net_of_expenses_1=after_tax_wealth_net_of_expenses_1+&
            (gross_wage_income_1-wage_income_tax_paid_1)
            
        consumption_1=(after_tax_wealth_net_of_expenses_1-future_financial_amount_1)/(1+consumption_tax)
        
        is_taxable_income_positive_1=0
        
        if (taxable_income_1>0) then
        
            is_taxable_income_positive_1=1
            
        end if
            
        if (consumption_1>0) then
        
            if (use_Frisch_elas_1==0) then

                y=(1/relative_share_of_leisure)*(1-relative_share_of_housing-relative_share_of_leisure)*&
                    (max_labor_supply-labor_supply_1)*curvature_of_labor_income_taxes*&
                    (labor_income_tax_rate+increase_in_labour_income_tax)*max_gross_wage_income_1*&
                    (taxable_income_1)**(curvature_of_labor_income_taxes-1)-consumption_1
                    
            else
            
                if (include_taxes_1==0) then
                
                    y=relative_share_of_leisure*(max_labor_supply-labor_supply_1)**(-Frisch_elasticity)-&
                        ((consumption_1**(1-relative_share_of_housing)*(space_lived_in_1)**(relative_share_of_housing))**&
                        (-risk_aversion_consumption))*((1-relative_share_of_housing)*(consumption_1)**(-relative_share_of_housing)*&
                        (space_lived_in_1)**(relative_share_of_housing))*(max_gross_wage_income_1)
                
                else
            
                    y=relative_share_of_leisure*(max_labor_supply-labor_supply_1)**(-Frisch_elasticity)-&
                        ((consumption_1**(1-relative_share_of_housing)*(space_lived_in_1)**(relative_share_of_housing))**&
                        (-risk_aversion_consumption))*((1-relative_share_of_housing)*(consumption_1)**(-relative_share_of_housing)*&
                        (space_lived_in_1)**(relative_share_of_housing))*(curvature_of_labor_income_taxes*&
                        (labor_income_tax_rate+increase_in_labour_income_tax)*max_gross_wage_income_1*(taxable_income_1)**&
                        (curvature_of_labor_income_taxes-1))
                        
                end if
                                
            end if
                    
        else
                
            can_consume_1=0                

        end if
        
    end subroutine

    subroutine calculate_after_tax_wealth(financial_index_1,future_financial_index_1,years_left_on_mortgage_1,&
        pay_mortgage_insurance_1,housing_status_index_1,future_housing_status_index_1,space_lived_in_index_1,&
        owner_to_owner_move_1,owner_to_renter_1,renter_to_owner_1,owner_to_owner_stay_1,renter_to_renter_1,&
        time_period,labor_supply_1,cutoff_taxable_income_1)
    
        use Global_Vars
        
        implicit none
        
        integer :: financial_index_1,future_financial_index_1,years_left_on_mortgage_1,space_lived_in_index_1,&
                    housing_status_index_1,future_housing_status_index_1,pay_mortgage_insurance_1,owner_to_owner_move_1,&
                    owner_to_renter_1,renter_to_owner_1,owner_to_owner_stay_1,renter_to_renter_1,time_period,&
                    mortgage_index_2,deduct_MID
                    
        real(8) :: tax_liability_with_mortgage_deductions_1,tax_liability_without_mortgage_deductions_1,&
                    cost_of_mortgage,cost_of_housing,deductions_claimed_1,&
                    lump_sum_transfer_lowest_income_1,fraction_rented_out,lump_sum_transfer_given,&
                    smallest_income,smallest_taxes,prepayment_penalty_1,origination_cost_1,&
                    fixed_cost_to_moving_1,imputed_rent_taxes_paid,amount_rented_out,labor_supply_1,&
                    cutoff_taxable_income_1,inheritance_1
                    
        inheritance_1=0
        
        if ((age_index>=min_age_inheritance) .AND. (age_index<=max_age_inheritance) .AND. &
            (include_bequests_in_govt_constraint==0))  then
        
            inheritance_1=one_income_state*employment_grid(age_index,employment_index)*proportion_inheritance+&
                (1-one_income_state)*fixed_employment_grid(fixed_employment_index)*proportion_inheritance
        
        end if
                    
        amount_rented_out=housing_size(future_housing_status_index_1)-housing_size(space_lived_in_index_1)
                                                        
        gross_wage_income=in_retirement*model_ss_benefits(fixed_employment_index,employment_index)+&
            (1-in_retirement)*(labor_supply_1*fixed_employment_grid(fixed_employment_index)*&
            employment_grid(age_index,employment_index)*labour_deterministic_efficiency(age_index))
                                                
        if (endogenous_rental_market==1) then
                    
            fraction_rented_out=amount_rented_out/housing_size(future_housing_status_index_1)
                
            if ((fraction_rented_out<0) .OR. (fraction_rented_out>1)) then
            
                write(*,*) "strne"
            
            end if
                
        else
        
            fraction_rented_out=0
        
        end if
        
        rental_income=0
        taxable_rental_income=0
        costs_of_managing_rental=0
                    
        if ((endogenous_rental_market==1) .AND. ((owner_to_owner_stay_1==1) .OR. &
            (owner_to_owner_move_1==1) .OR. (renter_to_owner_1==1))) then
            
            if (((amount_rented_out>=smallest_house_size) .AND. (discontinuous_landlords==1)) .OR. &
                ((amount_rented_out>0) .AND. (discontinuous_landlords==0))) then
                        
                rental_income=rent(time_period)*amount_rented_out
                
                if (allow_negative_rental_income==0) then
                
                    if (rental_income<0) then
                    
                        rental_income=large_negative_num
                        
                    end if
                    
                end if          
                    
                costs_of_managing_rental=rental_management_costs*discontinuous_landlords*&
                    (amount_rented_out/smallest_house_size)**disutility_of_landlords+&
                    rental_management_costs*((1-discontinuous_landlords)*&
                    (1+amount_rented_out)**(disutility_of_landlords)-1)+landlord_fixed_costs
                    
!                if (costs_of_managing_rental>landlord_fixed_costs) then
!                
!                    write(*,*) "not cocrect"
!                    
!                end if
                
                if (amount_rented_out==0) then
                
                    write(*,*) "wrong"
                    
                end if
                                                        
                taxable_rental_income=rental_income-housing_price(time_period)*amount_rented_out*&
                    (property_tax(time_period)+property_tax_on_rental_housing+housing_depreciation_rate)-&
                    fraction_rented_out*(-min(financial_grid(mortgage_index_2),0.d0))*&
                    (pay_mortgage_insurance_1*mortgage_insurance_premium+mortgage_interest_rate(time_period))-&
                    costs_of_managing_rental
                    
                if (allow_negative_taxable_rental_income==0) then
                
                    if (taxable_rental_income<0) then
                    
!                        rental_income=large_negative_num
                        
                    end if
                    
                end if
                                 
            end if
                                           
        end if
        
        if ((amount_rented_out>0) .AND. (rental_income==0)) then
                
            write(*,*) "wrong 2"
            
        end if
        
        if (include_rental_income==0) then
        
            rental_income=0
            taxable_rental_income=0
            costs_of_managing_rental=0
                                 
        end if
                                 
        total_household_working_wealth=max(financial_grid(financial_index_1),0.d0)
                          
        imputed_rent_taxes_paid=0
        
        if (tax_imputed_rent==1) then
        
            if (endogenous_rental_market==0) then
        
                if (years_left_on_mortgage_index>1) then
                
                    imputed_rent_taxes_paid=investment_income_tax*&
                        max(housing_size(housing_status_index)*(rent(time_period)-&
                        housing_price(time_period)*housing_depreciation_rate),0.d0)
                
                end if
                
            else
            
                if (add_imputed_rents_to_labor_income==1) then
                
                    if ((owner_to_owner_move_1==1) .OR. (owner_to_owner_stay_1==1) .OR. (renter_to_owner_1==1)) then
                
                        imputed_rent_taxes_paid=max(housing_size(space_lived_in_index_1)*&
                            (rent(time_period)-housing_price(time_period)*housing_depreciation_rate),0.d0)
                            
                    end if
                    
                else
                    
                    if ((owner_to_owner_move_1==1) .OR. (owner_to_owner_stay_1==1) .OR. (renter_to_owner_1==1)) then
                
                        imputed_rent_taxes_paid=investment_income_tax*max(housing_size(space_lived_in_index_1)*&
                            (rent(time_period)-housing_price(time_period)*housing_depreciation_rate),0.d0)
                            
                    end if
                
                end if
                
            end if
        
        end if
                                        
        investment_income=total_household_working_wealth*risk_free_rate(time_period)
                                                                                                                          
        investment_income_tax_paid=investment_income_tax*&
            max(investment_income+(1-tax_rental_income_with_labor_income)*taxable_rental_income,0.d0)
                                                
        mortgage_index_2=min(financial_index_1,negative_financial_grid_size)
                     
        deduct_MID=0
        deductions_claimed_1=0
        tax_liability_with_mortgage_deductions_1=0
        tax_liability_without_mortgage_deductions_1=0
        mortgage_deductions_tax_rebates=0
                                    
        if ((years_left_on_mortgage_1>1) .AND. ((mortgage_deductibility>0) .OR. (property_tax_deductibility>0))) then
                            
            deductions_claimed_1=&
                (mortgage_deductibility*min((1-fraction_rented_out)*(-min(financial_grid(mortgage_index_2),0.d0)),&
                max_MID_debt*(is_counterfactual*average_labor_income_calibrated+&
                (1-is_counterfactual)*model_average_labour_income(time_period)))*(pay_mortgage_insurance_1*&
                mortgage_insurance_premium+mortgage_interest_rate(time_period))+(1-fraction_rented_out)*&
                (property_tax_deductibility*property_tax(time_period)+include_housing_depreciation_in_housing_costs*&
                housing_depreciation_rate)*housing_price(time_period)*housing_size(housing_status_index_1))
                
            deductions_claimed_1=max(deductions_claimed_1,0.d0)
                
            if (deductions_claimed_1>standard_deduction) then
                
                deduct_MID=1
                                                                                    
                tax_liability_with_mortgage_deductions_1=&
                    max(max(gross_wage_income+tax_rental_income_with_labor_income*taxable_rental_income+&
                    add_imputed_rents_to_labor_income*imputed_rent_taxes_paid-&
                    personal_exemption-deductions_claimed_1,0.d0)-&
                    (labor_income_tax_rate+increase_in_labour_income_tax)*&
                    max(gross_wage_income+tax_rental_income_with_labor_income*taxable_rental_income+&
                    add_imputed_rents_to_labor_income*imputed_rent_taxes_paid-&
                    personal_exemption-deductions_claimed_1,0.d0)**curvature_of_labor_income_taxes,0.d0)
                                
                tax_liability_without_mortgage_deductions_1=&
                    max(max(gross_wage_income+tax_rental_income_with_labor_income*taxable_rental_income+&
                    add_imputed_rents_to_labor_income*imputed_rent_taxes_paid-&
                    personal_exemption-standard_deduction,0.d0)-&
                    (labor_income_tax_rate+increase_in_labour_income_tax)*&
                    max(gross_wage_income+tax_rental_income_with_labor_income*taxable_rental_income+&
                    add_imputed_rents_to_labor_income*imputed_rent_taxes_paid-&
                    personal_exemption-standard_deduction,0.d0)**curvature_of_labor_income_taxes,0.d0)
                    
                mortgage_deductions_tax_rebates=max(tax_liability_without_mortgage_deductions_1-&
                    tax_liability_with_mortgage_deductions_1,0.d0)
            
            end if
                     
        end if

        cutoff_taxable_income_1=personal_exemption+deduct_MID*deductions_claimed_1+(1-deduct_MID)*standard_deduction

        if ((taxable_rental_income>0) .AND. (endogenous_rental_market==0)) then

            write(*,*) "Errorr"

        end if
                    
        wage_income_tax_paid=max(max(gross_wage_income+tax_rental_income_with_labor_income*taxable_rental_income+&
            add_imputed_rents_to_labor_income*imputed_rent_taxes_paid-&
            personal_exemption-deduct_MID*deductions_claimed_1-(1-deduct_MID)*standard_deduction,0.d0)-&
            (labor_income_tax_rate+increase_in_labour_income_tax)*&
            max(gross_wage_income+tax_rental_income_with_labor_income*taxable_rental_income-personal_exemption+&
            add_imputed_rents_to_labor_income*imputed_rent_taxes_paid-&
            deduct_MID*deductions_claimed_1-(1-deduct_MID)*standard_deduction,0.d0)**&
            curvature_of_labor_income_taxes,0.d0)
            
        lump_sum_transfer_lowest_income_1=0
        
        if ((provide_subsidy>0) .AND. (employment_index<=1) .AND. &
            (fixed_employment_index<=1) .AND. (renter_to_renter_1==1)) then
        
            smallest_income=employment_grid(1,1)
            smallest_taxes=smallest_income-(labor_income_tax_rate+increase_in_labour_income_tax)*&
                smallest_income**(curvature_of_labor_income_taxes)
        
            lump_sum_transfer_lowest_income_1=&
                (max(financial_grid(financial_index_1),0.d0)+smallest_income-smallest_taxes-&
                (is_counterfactual*rent_calibrated+(1-is_counterfactual)*rent(time_period))*&
                housing_size(0))/smallest_income
                
            if (lump_sum_transfer_lowest_income_1<minimum_consumption_lowest_income) then
            
                lump_sum_transfer_lowest_income_1=smallest_income*&
                    (minimum_consumption_lowest_income-lump_sum_transfer_lowest_income_1)
                    
            else
            
                lump_sum_transfer_lowest_income_1=0
                    
            end if
                                 
        end if
                                            
        fixed_cost_to_moving_1=0
                              
        if (renter_to_renter_1==1) then
        
            origination_cost_1=0
            prepayment_penalty_1=0
                
            cost_of_housing=0
            
            if (include_moving_back==1) then
            
                if (future_housing_status_index_1==lowest_housing_grid_point) then
                
                    rent_spending=0
                    
                else
                        
                    rent_spending=rent(time_period)*housing_size(future_housing_status_index_1)
                    
                end if
                
            else
            
                rent_spending=rent(time_period)*housing_size(future_housing_status_index_1)
                
            end if
                        
            if (((future_housing_status_index_1 .NE. housing_status_index_1) .OR. &
                (years_left_on_mortgage_index .NE. 1)) .AND. (age_index>1)) then
                        
                fixed_cost_to_moving_1=fixed_cost_to_moving
                            
            end if
                                               
            property_taxes_paid=0
            
            housing_depreciation_expenditures=0
            
            cost_of_mortgage=0  
            
            if (rent_voucher_to_lowest_incomes==1) then
            
               if (employment_index<=lowest_income_given_rent_voucher) then
            
                    lump_sum_transfer_given=min(rent_spending,lump_sum_transfer(time_period))
                    
                else
                
                    lump_sum_transfer_given=0
                
                end if
            
            else
            
                if (give_lump_sum_to_renters==1) then
                
                    lump_sum_transfer_given=lump_sum_transfer(time_period)
                    
                else
                            
                    lump_sum_transfer_given=0
                            
                end if
                
            end if
            
        elseif (renter_to_owner_1==1) then
        
            origination_cost_1=0
        
            if (financial_grid(future_financial_index_1)<0) then
        
                origination_cost_1=mortgage_origination_cost*&
                    (is_counterfactual*average_labor_income_calibrated+&
                    (1-is_counterfactual)*model_average_labour_income(time_period))
                    
            end if
                
            prepayment_penalty_1=0
                           
            cost_of_housing=(1+cost_of_buying)*housing_price(time_period)*housing_size(future_housing_status_index_1)
        
            rent_spending=0
                            
            property_taxes_paid=housing_price(time_period)*property_tax_on_rental_housing*amount_rented_out
                                                                   
            housing_depreciation_expenditures=0
                                   
            cost_of_mortgage=0
                       
            if (give_lump_sum_to_owners==1) then
            
                lump_sum_transfer_given=lump_sum_transfer(time_period)
                
            else
                        
                lump_sum_transfer_given=0
                        
            end if
                                                                                        
        elseif (owner_to_owner_stay_1==1) then
                
            if (imitate_long_term_mortgages==1) then
                                    
                origination_cost_1=0  
                prepayment_penalty_1=0
                
                if (financial_grid(financial_index_1)<0) then
                
                    if (financial_grid(future_financial_index_1)<&
                        (financial_grid(financial_index_1)*(1-prepayment_penalty_starts_from))) then
                        
                        origination_cost_1=mortgage_origination_cost*&
                            (is_counterfactual*average_labor_income_calibrated+&
                            (1-is_counterfactual)*model_average_labour_income(time_period))
                        
                        prepayment_penalty_1=penalty_months_of_interest*mortgage_interest_rate(time_period)*&
                            (-min(financial_grid(financial_index_1),0.d0))*(1-prepayment_penalty_starts_from)
                                                    
                    else
                    
                        prepayment_penalty_1=penalty_months_of_interest*mortgage_interest_rate(time_period)*&
                            max(min(financial_grid(future_financial_index_1),0.d0)-financial_grid(financial_index_1)*&
                            (1-prepayment_penalty_starts_from),0.d0)
                            
                    end if
                    
                else
                
                    if (financial_grid(future_financial_index_1)<0) then
                
                        origination_cost_1=mortgage_origination_cost*&
                            (is_counterfactual*average_labor_income_calibrated+&
                            (1-is_counterfactual)*model_average_labour_income(time_period))
                            
                    end if
                    
                end if
                                                  
            else
            
                origination_cost_1=0
                prepayment_penalty_1=0
            
            end if
                            
            cost_of_housing=housing_price(time_period)*housing_size(housing_status_index_1)
        
            rent_spending=0
                                            
            gross_housing_value=housing_price(time_period)*housing_size(housing_status_index_1)
                            
            property_taxes_paid=property_tax(time_period-solve_transition_dynamics)*&
                housing_price(time_period-solve_transition_dynamics)*housing_size(housing_status_index_1)+&
                housing_price(time_period)*property_tax_on_rental_housing*amount_rented_out
                                                                                              
            housing_depreciation_expenditures=&
                housing_depreciation_rate*housing_price(time_period-solve_transition_dynamics)*housing_size(housing_status_index_1)
                                   
            cost_of_mortgage=-min(financial_grid(financial_index_1),0.d0)*&
                (1+mortgage_interest_rate(time_period)+pay_mortgage_insurance_1*mortgage_insurance_premium)
                            
            if (give_lump_sum_to_owners==1) then
            
                lump_sum_transfer_given=lump_sum_transfer(time_period)
                
            else
                        
                lump_sum_transfer_given=0
                        
            end if
                            
        elseif (owner_to_owner_move_1==1) then
        
            if (imitate_long_term_mortgages==1) then
                            
                if (financial_grid(future_financial_index_1)<0) then
                            
                    origination_cost_1=mortgage_origination_cost*&
                        (is_counterfactual*average_labor_income_calibrated+&
                        (1-is_counterfactual)*model_average_labour_income(time_period))
                        
                else
                
                    origination_cost_1=0
                
                end if
                    
                prepayment_penalty_1=penalty_months_of_interest*mortgage_interest_rate(time_period)*&
                    (-min(financial_grid(financial_index_1),0.d0))*(1-prepayment_penalty_starts_from)
                                          
            else
            
                origination_cost_1=0
                prepayment_penalty_1=0
            
            end if
        
            rent_spending=0
                                            
            gross_housing_value=housing_price(time_period)*housing_size(housing_status_index_1)
                            
            property_taxes_paid=property_tax(time_period-solve_transition_dynamics)*&
                housing_price(time_period-solve_transition_dynamics)*housing_size(housing_status_index_1)+&
                housing_price(time_period)*property_tax_on_rental_housing*amount_rented_out
                   
            cost_of_housing=(1+cost_of_buying)*housing_price(time_period)*&
                housing_size(future_housing_status_index_1)+cost_of_selling*(housing_price(time_period)*&
                housing_size(housing_status_index_1))+land_transfer_fee*&
                (housing_price(time_period)*housing_size(housing_status_index_1))
                                                                               
            housing_depreciation_expenditures=&
                housing_depreciation_rate*housing_price(time_period-solve_transition_dynamics)*housing_size(housing_status_index_1)
                                            
            cost_of_mortgage=-min(financial_grid(financial_index_1),0.d0)*&
                (1+mortgage_interest_rate(time_period)+&
                pay_mortgage_insurance_1*mortgage_insurance_premium)
                     
            if (give_lump_sum_to_owners==1) then
            
                lump_sum_transfer_given=lump_sum_transfer(time_period)
                
            else
                        
                lump_sum_transfer_given=0
                        
            end if
                                    
        elseif (owner_to_renter_1==1) then
        
            origination_cost_1=0
        
            if (imitate_long_term_mortgages==1) then
            
                prepayment_penalty_1=penalty_months_of_interest*mortgage_interest_rate(time_period)*&
                    (-min(financial_grid(financial_index_1),0.d0))*(1-prepayment_penalty_starts_from)
                                    
            else
                
                prepayment_penalty_1=0
            
            end if
            
            if (include_moving_back==1) then
            
                if (future_housing_status_index_1==lowest_housing_grid_point) then
                
                    rent_spending=0
                
                else
                
                    rent_spending=rent(time_period)*housing_size(future_housing_status_index_1)
                
                end if
                
            else
            
                rent_spending=rent(time_period)*housing_size(future_housing_status_index_1)
            
            end if
                                                       
            gross_housing_value=housing_price(time_period)*housing_size(housing_status_index_1)
            
            property_taxes_paid=property_tax(time_period-solve_transition_dynamics)*&
                housing_price(time_period-solve_transition_dynamics)*housing_size(housing_status_index_1)
                                             
            cost_of_housing=cost_of_selling*housing_price(time_period)*&
                housing_size(housing_status_index_1)+land_transfer_fee*(housing_price(time_period)*&
                housing_size(housing_status_index_1))
                                    
            housing_depreciation_expenditures=&
                housing_depreciation_rate*housing_price(time_period-solve_transition_dynamics)*housing_size(housing_status_index_1)
                        
            cost_of_mortgage=-min(financial_grid(financial_index_1),0.d0)*(1+mortgage_interest_rate(time_period)+&
                pay_mortgage_insurance_1*mortgage_insurance_premium)
            
            
            if (rent_voucher_to_lowest_incomes==1) then
                
                if (employment_index<=lowest_income_given_rent_voucher) then
            
                    lump_sum_transfer_given=min(rent_spending,lump_sum_transfer(time_period))
                    
                else
                
                    lump_sum_transfer_given=0
                    
                end if
            
            else
            
                if (give_lump_sum_to_renters==1) then
                
                    lump_sum_transfer_given=lump_sum_transfer(time_period)
                    
                else
                            
                    lump_sum_transfer_given=0
                            
                end if
                
            end if
                                                                                    
        end if
                
        if ((tax_imputed_rent==0) .AND. (imputed_rent_taxes_paid>0)) then
        
            write(*,*) "oops"
            
        end if
        
        if (fixed_cost_to_moving_1>0) then
        
            write(*,*) "ooop233"
            
        end if
        
        if (lump_sum_transfer_lowest_income_1>0) then
        
            write(*,*) "oppopos"
            
        end if
                                                                                
        after_tax_wealth_net_of_expenses=gross_wage_income-wage_income_tax_paid+&
            investment_income-investment_income_tax_paid+rental_income-costs_of_managing_rental+total_household_wealth-& 
            property_taxes_paid-housing_depreciation_expenditures-rent_spending-cost_of_mortgage-&
            cost_of_housing+lump_sum_transfer_given+lump_sum_transfer_lowest_income_1-&
            origination_cost_1-prepayment_penalty_1-fixed_cost_to_moving_1-imputed_rent_taxes_paid+&
            inheritance_1
                        
    end subroutine
        
    subroutine internal_computations&
