        do j=2,employment_grid_size-1
        
            temp_upper=(1+erf(((z_grid_by_age(1,j)+0.5*width(1))/sqrt(variance_by_age(1)))/sqrt(2.d0)))/2
            temp_lower=(1+erf(((z_grid_by_age(1,j)-0.5*width(1))/sqrt(variance_by_age(1)))/sqrt(2.d0)))/2
