    subroutine set_SS_benefits(time_period)
    
        use Global_Vars
        
        implicit none
        
        integer :: time_period
        real(8), dimension(fixed_employment_grid_size,employment_grid_size) :: sum_of_distribution_fixed_and_regular_employment
        
        sum_of_distribution=0
        
        do i=1,2
                
            sum_of_distribution=sum_of_distribution+&
                sum(distribution_stationary(i)%vector_transition_real(time_period-solve_transition_dynamics,:,:,:,:,:))
            
        end do
        
        sum_of_distribution_fixed_and_regular_employment=0
        
        do i=1,2
        
            do fixed_employment_index=1,fixed_employment_grid_size
            
                do employment_index=1,employment_grid_size
                
                    sum_of_distribution_fixed_and_regular_employment(fixed_employment_index,employment_index)=&
                        sum_of_distribution_fixed_and_regular_employment(fixed_employment_index,employment_index)+&
                        sum(distribution_stationary(i)%vector_transition_real&
                        (time_period-solve_transition_dynamics,:,:,fixed_employment_index,employment_index,:))
                    
                end do
                
            end do
            
        end do
                                   
        model_ss_benefits=0
                                    
        do employment_index=1,employment_grid_size
        
            do fixed_employment_index=1,fixed_employment_grid_size
        
                probability_came_from_past_employment_index_1=0

                model_lifetime_income_conditional_on_last_period_shock(fixed_employment_index,employment_index)=0
                
                probability_came_from_past_employment_index_1(fixed_employment_index,employment_index)=1
        
                do age_index=(life_span-retirement_span),1,-1
            
                    probability_came_from_past_employment_index_2=0
                        
                    do past_employment_index_1=1,employment_grid_size
                                                                                
                        model_lifetime_income_conditional_on_last_period_shock(fixed_employment_index,employment_index)=&
                            model_lifetime_income_conditional_on_last_period_shock(fixed_employment_index,employment_index)+&
                            probability_came_from_past_employment_index_1(fixed_employment_index,past_employment_index_1)*&
                            (include_labor_supply*avg_labor_supply_data*max_labor_supply+(1-include_labor_supply))*&
                            fixed_employment_grid(fixed_employment_index)*employment_grid(age_index,past_employment_index_1)*&
                            labour_deterministic_efficiency(age_index)
                        
                        do past_employment_index_2=1,employment_grid_size
                        
                            probability_came_from_past_employment_index_2(fixed_employment_index,past_employment_index_2)=&
                                probability_came_from_past_employment_index_2(fixed_employment_index,past_employment_index_2)+&
                                probability_came_from_past_employment_index_1(fixed_employment_index,past_employment_index_1)*&
                                employment_process(max(age_index-1,1),past_employment_index_2,past_employment_index_1)/&
                                sum(employment_process(max(age_index-1,1),:,past_employment_index_1))
                        
                        end do
                                                                                
                    end do
                    
                end do
                                        
                probability_came_from_past_employment_index_1=&
                    probability_came_from_past_employment_index_2
                    
                model_lifetime_income_conditional_on_last_period_shock&
                    (fixed_employment_index,employment_index)=&
                    model_lifetime_income_conditional_on_last_period_shock&
                    (fixed_employment_index,employment_index)/(life_span-retirement_span)
                                            
            end do
                        
        end do
                          
        ! Computing the average lifetime income conditional on the last period shock
        model_average_lifetime_income_conditional_on_last_period_shock=0
            
        do fixed_employment_index=1,fixed_employment_grid_size
            
            do employment_index=1,employment_grid_size
                        
                if (loop_count<1) then
                
                    model_average_lifetime_income_conditional_on_last_period_shock=&
                        model_average_lifetime_income_conditional_on_last_period_shock+&
                        model_lifetime_income_conditional_on_last_period_shock(fixed_employment_index,employment_index)
                    
                else
            
                    model_average_lifetime_income_conditional_on_last_period_shock=&
                        model_average_lifetime_income_conditional_on_last_period_shock+&
                        model_lifetime_income_conditional_on_last_period_shock(fixed_employment_index,employment_index)*&
                        (sum_of_distribution_fixed_and_regular_employment(fixed_employment_index,employment_index)/&
                        sum_of_distribution)
                
                end if
                
            end do
                                  
        end do
                
        do fixed_employment_index=1,fixed_employment_grid_size
                        
            ! Looping over all possible last period employment states
            do employment_index=1,employment_grid_size
                
                if ((model_lifetime_income_conditional_on_last_period_shock(fixed_employment_index,employment_index)/&
                    model_average_lifetime_income_conditional_on_last_period_shock)<=0.3) then
                    
                    ! Computes social security benefits
                    model_ss_benefits(fixed_employment_index,employment_index)=&
                        0.9*(model_lifetime_income_conditional_on_last_period_shock&
                        (fixed_employment_index,employment_index)/&
                        model_average_lifetime_income_conditional_on_last_period_shock)
                    
                elseif (((model_lifetime_income_conditional_on_last_period_shock(fixed_employment_index,employment_index)/&
