            write(2,*) "nu,", relative_share_of_leisure
            write(2,*) "advantage_to_owning,", advantage_to_owning
            write(2,*) "discount factor,", discount_factor
            write(2,*) "mu,", discount_factor_on_child
            write(2,*) "tau_ell,", labor_income_tax_rate
